package Feb23;

public class ArthEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//Add
		int salary = 50000;
		int bonus = 5000;
		int totalSalary = salary + bonus;
		System.out.println("Total salary of the emp is : " + totalSalary);
		
		//Sub
		int loan = 10000;
		int netSalary = totalSalary - loan;
		System.out.println("Net salary of the emp is : " + netSalary);
		
		//Mul
		int schoolFeePerMonth = 5000;
		int totalNoOfMonths = 10;
		int totalSchoolFee = schoolFeePerMonth * totalNoOfMonths;
		System.out.println("Total school fee : " + totalSchoolFee);
		
		//Div
		int TeacherSalaryPerYear = 120000;
		int totalMonths = 12;
		int MonthlySalary = TeacherSalaryPerYear/totalMonths;
		System.out.println("Montly salary is " + MonthlySalary);
		
		
		//add
		int a = 1;
		int b = 2;
		int c=a+b;
		System.out.println("addition is :" + c );
		
	}

}
